<template>
	<view v-if='show' class='zz box_hcc' @tap='closeFn'>
		<view class='body'>
			<view class='title'>人脸识别</view>
			<view class='camera_body'>
				<camera
					class='camera' 
					mode="normal" 
					device-position="front" 
					flash="off" 
					@error="error" 
				></camera>
				<cover-image :src='imgSrc' class='cover_img'></cover-image>
			</view>
			<view class='btn box_hcc' hover-class='hover' :class='canTap' @tap.native.stop='startRecord'>{{btnText}}</view>
		</view>
	</view>
</template>

<script>
	import bas64ToLocatImg from './base64ToLocaalImg.js';
	export default {
		props:{
			times:{
				type:String,
				value:'10'
			},
			uploadServerUrl:{
				type:String,
				value:''
			},
			sign:{
				type:String,
				value:''
			},
			show:{
				type:Boolean,
				value:false
			}
		},
		data() {
			return {
				imgSrc:'',
				canTap:'',
				btnText:'开始识别'
			}
		},
		watch:{
			show(newVal,oldVal){
				console.log(oldVal,newVal)
			}
		},
		onReady() {
			this.createImg().then(rs=>{console.log(rs)});
		},
		methods: {
			async createImg(){
				let base64 = 'data:image/png;base64,';
				this.imgSrc = await bas64ToLocatImg(base64);
			},
			startRecord(){
				if(this.canTap){return;}
				this.btnText = '识别中';
				
				let camera = uni.createCameraContext(this),
					_this = this;
				
				this.canTap = 'notCanTap';
					
				camera.startRecord({
					timeoutCallback:30,
					success(){
						
					},
					fail(e){
						_this.error('识别失败');
						_this.btnText = '开始识别';
						_this.canTap = '';
					}
				});
				
	
				setTimeout(e=>{
					camera.stopRecord({
						compressed:true,
						success(res){
							let path = res.tempVideoPath;
							_this.uploadFile(path);
						},
						fail(e){
							_this.error('识别失败');
							_this.btnText = '开始识别';
							_this.canTap = '';
						}
					});
				},this.times*1000)
				
			},
			error(msg){
				msg = msg || '用户未授权';
				uni.showModal({
					title: '更新提示',
					content: msg,
					showCancel:false,
					success(res) {

					}
				});
			},
			uploadFile(path){
				uni.showLoading({
				    title: '识别中',
					mask:true
				});
				
				let _this = this;
				
				uni.uploadFile({
					url: this.uploadServerUrl, //仅为示例，非真实的接口地址
					filePath: path,
					name: 'file',
					formData: {
						sign:this.sign
					},
					success: (uploadFileRes) => {
						uni.hideLoading();
						let rs = uploadFileRes.data;
						rs = JSON.parse(rs);
						if(rs.code != 1){
							_this.error(rs.msg);
							_this.canTap = '';
						}else{
							let src = rs.data || {};
							src = src.fullurl;
							_this.uploadOk(src);
						}
					},
					fail(e){
						uni.hideLoading();
						_this.error('识别失败!');
						// _this.error(JSON.stringify(e));
						_this.btnText = '开始识别';
						_this.canTap = '';
					}
				});
			},
			uploadOk(){
				// this.show = false;
				
				this.$emit('success','');
				// console.log('success');
			},
			closeFn(){
				this.$emit('close','')
				// this.show = false;
			}
		}
	}
</script>

<style>
	@import url("../../publish/box.css");
	.zz{
		position: fixed; width: 100%; height: 100%; z-index:999999;
		padding-bottom:200rpx; box-sizing: border-box;
	}
	.body{
		width: 552rpx;
		padding:0 97rpx 30rpx 97rpx; 
		background: #fff;
		box-sizing: border-box;
	}
	.title{
		font-size: 32rpx;
		color: #212121;
		padding:29rpx 0 39rpx 0;
		text-align: center;
	}
	.camera,
	.camera_body{
		width: 357rpx; height: 357rpx; display: block;
	}
	.camera_body{
		position: relative;
	}
	.cover_img{
		position: absolute; left:0; top:0;
		width: 357rpx; height: 357rpx; z-index: 10;
	}
	.btn{
		width:371rpx; height: 80rpx; background: #6097fc;
		font-size: 32rpx;
		font-weight: bold;
		color: #FFFFFF;
		border-radius: 111rpx;
		margin-top: 60rpx;
	}
	.hover{
		opacity: 0.7;
	}
	.notCanTap{
		background: #ccc;
	}
</style>
